import React from "react";

function BuyNow(product,onClose)
{
  const handleCheckout = () => {
        alert(`Proceeding to checkout for ${product.name}`);
      };
  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100 overlay">
      <div className="bg-white p-6 rounded-lg shadow-lg max-w-md text-center">
        <h2 className="text-2xl font-bold text-gray-800">{product.name}</h2>
        <img
          src={product.image}
          alt={product.name}
          className="w-64 mx-auto my-4 rounded-lg shadow-md"
        />
        <p className="text-lg">
          <span className="font-semibold text-orange-500">Price:</span> Rs.{product.price}
        </p>
        <p className="text-gray-700">
          <span className="font-semibold">Size:</span> {product.size}
        </p>
        <p className="text-gray-700">
          <span className="font-semibold">Color:</span> {product.color}
        </p>
        <p className="text-gray-600 mt-2">{product.description}</p>
        <button
          onClick={handleCheckout}
          className="mt-4 bg-red-500 text-white px-6 py-2 rounded-lg shadow-md hover:bg-red-600 transition duration-300"
        >
          Proceed to Checkout
        </button>
      </div>
    </div>
  );
}
