import React, { useState } from "react";
import BuyNow from "./BuyNow";

function ProductDetails({ product, onClose }) {
  const [showBuyNow, setShowBuyNow] = useState(false);
  console.log("Product in ProductDetails:", product);

  function buyNowClicked() {
    setShowBuyNow(true);
  }

  if (showBuyNow) {
    return <BuyNow product={product} onClose={() => setShowBuyNow(false)} />;
  }

  return (
    <div className="product-details-overlay">
      <div className="product-details">
        <div className="Prod_title">
          <h2 className="product-title">{product.title}</h2>
          <button className="close-button" onClick={onClose}>
            &times;
          </button>
        </div>

        <img className="product-image" src={product.imgSrc} alt={product.title} />
        <p className="product-price">
          <strong>Price:</strong> Rs.{product.price}
        </p>
        <p className="product-size">
          <strong>Size:</strong> {product.size}
        </p>
        <p className="product-color">
          <strong>Color:</strong> {product.color}
        </p>
        <p className="product-description">
          <strong>Description:</strong> {product.description}
        </p>

        <div className="product-buttons">
          <button className="add-to-cart-button">Add to Cart</button>
          <button className="buy-now-button" onClick={buyNowClicked}>
            Buy Now
          </button>
        </div>
      </div>
    </div>
  );
}

export default ProductDetails;
