import React from "react";
import { useLocation, useNavigate } from "react-router-dom";

function BuyNow() {
  const location = useLocation();
  const navigate = useNavigate();
  const product = location.state?.product;

  if (!product) {
    return <h2 className="text-center text-red-500 font-bold">Error: No product selected.</h2>;
  }

  const handleCheckout = () => {
    alert(`Proceeding to checkout for ${product.title}`);
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="bg-white p-6 rounded-lg shadow-lg max-w-lg">
        <h2 className="text-2xl font-bold text-gray-800 text-center">Checkout</h2>

        <div className="flex flex-col md:flex-row items-center mt-4">
          <img
            src={product.imgSrc}
            alt={product.title}
            className="w-40 h-40 object-cover rounded-lg shadow-md"
          />
          <div className="ml-6">
            <h3 className="text-xl font-semibold">{product.title}</h3>
            <p className="text-lg text-gray-700"><strong>Price:</strong> Rs.{product.price}</p>
            <p className="text-gray-600"><strong>Size:</strong> {product.size}</p>
            <p className="text-gray-600"><strong>Color:</strong> {product.color}</p>
          </div>
        </div>

        <div className="mt-6">
          <p className="text-gray-700"><strong>Shipping Address:</strong></p>
          <input
            type="text"
            placeholder="Enter your address"
            className="w-full p-2 border rounded-lg mt-2"
          />
        </div>

        <div className="flex justify-between mt-6">
          <button
            onClick={() => navigate(-1)}
            className="bg-gray-500 text-white px-4 py-2 rounded-lg shadow-md hover:bg-gray-600"
          >
            Back
          </button>
          <button
            onClick={handleCheckout}
            className="bg-green-500 text-white px-6 py-2 rounded-lg shadow-md hover:bg-green-600"
          >
            Proceed to Checkout
          </button>
        </div>
      </div>
    </div>
  );
}

export default BuyNow;
